mapper.select:
select

from `doc_app_version`

	
		and `id` = #{id,jdbcType=INTEGER}
	
	
		and `app_id` = #{appId,jdbcType=INTEGER}
	
	
		and `version` = #{version,jdbcType=VARCHAR}
	
	
		and `is_default` = #{isDefault}
	
	


	ORDER BY ${sortColumn}
	
		${sortType}
	

limit #{startRow},#{pageSize}

select
count(1)
from `doc_app_version`

	
		and `id` = #{id,jdbcType=INTEGER}
	
	
		and `app_id` = #{appId,jdbcType=INTEGER}
	
	
		and `version` = #{version,jdbcType=VARCHAR}
	
	
		and `is_default` = #{isDefault}

select
	`version`
from `doc_app_version`

	
		and `id` = #{id,jdbcType=INTEGER}
	
	
		and `app_id` = #{appId,jdbcType=INTEGER}
	
	
		and `version` = #{version,jdbcType=VARCHAR}
	
	
		and `is_default` = #{isDefault}
	


	ORDER BY ${sortColumn}
	
		${sortType}
